INSERT INTO `blogs` (`id`, `content`, `user_id`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Cà Phê Sữa Dá: Coffee the Vietnamese Way', 1, 0, '2022-08-20 12:47:10', '2022-08-20 12:47:10'),
(2, 'San Francisco\'s Best Local Markets by Neighborhood', 1, 0, '2022-08-20 12:49:53', '2022-08-20 12:49:53'),
(3, 'Khi đến với Đà Lạt điều không thể bỏ qua đó là tìm đến những quán cafe ngon có view thật đẹp để check in cùng gia đình và bạn bè. Để lựa chọn quán cafe ngon ở Đà Lạt có view đẹp để check-in', 1, 0, '2022-08-20 12:56:04', '2022-08-20 12:56:04'),
(4, 'Những quán cà phê mới đẹp ở Đà Lạt nằm ẩn mình trong những con hẻm, trên những con dốc hay trên ngọn đồi cao. Những quán cà phê mới với không gian nhỏ xinh chắc chắn là địa điểm bạn không thể bỏ qua mỗi khi có dịp đến thành phố sương mù.', 1, 0, '2022-08-20 12:59:06', '2022-08-20 12:59:06'),
(5, 'Bộ sưu tập 1 vài công thức trà bỏ túi cho chị em, hoặc quán cafe dùng để pha chế', 1, 0, '2022-08-20 13:00:14', '2022-08-20 13:00:14'),
(6, 'Egg yolk coffee, or ca phe trung, can be found in a number of cities throughout Vietnam. With that being said, there is certainly no shortage of this traditional delicacy in Vietnam’s capital city of Hanoi. Discover which cafes you should visit for the best egg coffee in Hanoi, Vietnam! #fallontravels #vietnamtravel', 4, 0, '2022-08-20 13:04:39', '2022-08-20 13:04:39'),
(7, 'Quán Coffee có thiết kế như một khối hộp gỗ nằm giữa đô thị vật liệu bê tông hỗn loạn', 4, 0, '2022-08-20 13:07:50', '2022-08-20 13:07:50'),
(8, 'Anh Kangbin Lee đã thổi một làn gió mới vào nghệ thuật trang trí khiến các cốc cà phê trở nên ấn tượng hơn rất nhiều.', 4, 0, '2022-08-20 13:08:31', '2022-08-20 13:08:31'),
(9, 'Sài Gòn đang quá đẹp trời, rủ rê hội bạn cà phê thôi nào: Dưới đây chính là 10 quán cực hợp để \'chill\' giữa tiết trời se mát này!', 4, 0, '2022-08-20 13:10:17', '2022-08-20 13:10:17'),
(10, 'LATTE MÈ ĐEN - Với vị thơm nồng đặc trưng của mè đen kết hợp cùng vanilla và sữa tươi, ly Latte mè đen sẽ là sự lựa chọn tuyệt vời cho buổi hẹn hò những ngày cuối năm Sài Gòn chớm lạnh.', 4, 0, '2022-08-20 13:16:25', '2022-08-20 13:16:25'),
(11, 'Món sinh tố tự làm ở nhà vừa ngon lại vừa rẻ, chất lượng hơn nhiều so với mua bên ngoài đấy, bạn thử làm các món sinh tố bổ dưỡng này cho c...', 4, 0, '2022-08-20 13:18:25', '2022-08-20 13:18:25'),
(12, '25 Charts Filled With Random Knowledge.', 2, 0, '2022-08-20 14:00:11', '2022-08-20 14:00:11'),
(13, 'In this article, we’ll show you how to roast coffee beans at home, but don’t worry, it’s not overly complicated; the process is completely beginner-friendly.', 2, 0, '2022-08-20 14:00:36', '2022-08-20 14:00:36'),
(14, 'Both contain antioxidants and anti-inflammatory properties, but one brew takes the win, says a new study.', 2, 0, '2022-08-20 14:01:01', '2022-08-20 14:01:01'),
(15, 'Ghé 9 quán cà phê vintage ở Sài Gòn - quay ngược thời gian, sống lại những tháng ngày xưa', 2, 0, '2022-08-20 14:02:51', '2022-08-20 14:02:51');
INSERT INTO `blog_comments` (`id`, `content`, `user_id`, `blog_id`, `status`, `created_at`, `updated_at`) VALUES
(1, 'Authentic Vietnamese coffee brewed with a traditional Phin filter and served with sweetened condensed milk over ice.', 4, 6, 0, '2022-08-20 13:15:38', '2022-08-20 13:15:38'),
(2, 'Cuối tuần ngồi cà phê \'ngày xưa\' ở Sài Gòn', 2, 3, 0, '2022-08-20 14:03:20', '2022-08-20 14:03:20'),
(3, 'Nắm chắc trong tay mẫu quán cafe đẹp với những phông nền sống ảo đẹp chết người là bạn đã thành công đến 90% khi kinh doanh quán cafe rồi đấy.', 2, 2, 0, '2022-08-20 14:04:26', '2022-08-20 14:04:26'),
(4, 'Nắm chắc trong tay mẫu quán cafe đẹp với những phông nền sống ảo đẹp chết người là bạn đã thành công đến 90% khi kinh doanh quán cafe rồi đấy.', 2, 2, 0, '2022-08-20 14:04:26', '2022-08-20 14:04:26'),
(5, 'Cafe ở Sài Gòn, người Sài Gòn mến mời!', 2, 1, 0, '2022-08-20 14:05:08', '2022-08-20 14:05:08'),
(6, 'Lùng ngay quán cà phê Địa Trung Hải vừa xuất hiện đã \"gây sốt\" diện rộng ở Sài Gòn', 3, 4, 0, '2022-08-20 23:09:19', '2022-08-20 23:09:19');
